package ConsolePros;/** * Generating string to some sub-string with certain length */public class GenerateSubstrings {    public static void main(String[] args) {        generateSubstrings("welcometojava", 3);    }    public static void generateSubstrings(String string, int k){        final int LENGTH = string.length();        for(int i = 0; i <= LENGTH - k; i++){            System.out.println(string.substring(i, i + k));        }    }}